mapper.select:
select admin_program_seq, program_id, parents_program_id, level, program_name, program_sort, use_yn, location ,reg_date ,update_date from t_admin_program where 1=1
AND substring(reg_date, 0, 5)||'-'||substring(reg_date, 5, 2)||'-'||substring(reg_date, 7, 2) BETWEEN #{searchStartDt} AND #{searchEndDt}
AND program_name LIKE CONCAT('%', #{searchText}, '%')
AND level = #{searchLevel}
order by program_sort desc, program_id asc
