mapper.update:
update t_admin_user
set 

  	  block_yn = #{blockYn,jdbcType=INTEGER}

where admin_user_seq = #{adminUserSeq,jdbcType=VARCHAR}

mapper.select:

select admin_user_seq, user_id, password, user_type_code, reg_date, update_date, 
password_error_count, password_error_date, password_lost_check, password_lost_date, 
lastlogin_date, block_yn, user_name
from t_admin_user
where user_id = #{userId,jdbcType=VARCHAR}
AND password= #{password,jdbcType=VARCHAR}
