mapper.update:

update t_notice
set		disp_yn = 'N'
where 	notice_seq 	<> #{noticeSeq,jdbcType=INTEGER}

mapper.select:
/* 앱공지 selecTNoticeAllList */ SELECT a.notice_seq ,a.notice_desc ,a.notice_file_path ,a.disp_yn ,a.board_seq ,a.ins_seq ,a.ins_dt ,a.upt_seq ,a.upt_dt FROM t_notice a WHERE 1=1
AND TO_CHAR(a.ins_dt, 'YYYY-MM-DD') BETWEEN #{searchStartDt} AND #{searchEndDt}
AND a.notice_desc LIKE CONCAT('%', #{searchText}, '%')
ORDER BY a.disp_yn DESC, a.notice_seq DESC
