mapper.delete:

delete from [realestateletter].[dbo].[COMMON_CODE]
where code_group = #{codeGroup,jdbcType=VARCHAR}
  and code_value = #{codeValue,jdbcType=VARCHAR}

mapper.update:

update [realestateletter].[dbo].[COMMON_CODE]
set code_name = #{codeName,jdbcType=VARCHAR},
  code_description = #{codeDescription,jdbcType=VARCHAR},
  reg_date = CONVERT(CHAR(19), getdate(), 20)
where code_group = #{codeGroup,jdbcType=VARCHAR}
  and code_value = #{codeValue,jdbcType=VARCHAR}

mapper.select:

select code_group, code_value, code_name, code_description, reg_date
from [realestateletter].[dbo].[COMMON_CODE]
where code_group = #{codeGroup,jdbcType=VARCHAR}
  and code_value = #{codeValue,jdbcType=VARCHAR}
