mapper.update:
/* 업적 사용여부 수정 upDateDefineWork */
UPDATE t_define_work 
SET use_yn = 'N',
    upt_seq = #{uptSeq,jdbcType=BIGINT},
    upt_dt = now() 
WHERE work_seq = #{upWorkSeq,jdbcType=INTEGER}

mapper.insert:
/* 업적등록 insertDefineWork */
INSERT INTO t_define_work
