mapper.update:
/* 업적승인여부  수정 upDateAllDefineWork*/
UPDATE t_define_work
         SET work_nm 				= #{workNm ,jdbcType=VARCHAR}
            ,work_condition 		= #{workCondition ,jdbcType=VARCHAR}
