mapper.select:
/* 메인 사용자 등록 막대챠트 getAnalysisChart2 */ select t.regit_cnt ,EXTRACT(DOW FROM t.ins_dt::date ) as week_cnt ,t.ins_dt ,t.os_type_aos ,t.os_type_ios ,( case when EXTRACT(DOW FROM t.ins_dt::date ) = 0 then '일' when EXTRACT(DOW FROM t.ins_dt::date ) = 1 then '월' when EXTRACT(DOW FROM t.ins_dt::date ) = 2 then '화' when EXTRACT(DOW FROM t.ins_dt::date ) = 3 then '수' when EXTRACT(DOW FROM t.ins_dt::date ) = 4 then '목' when EXTRACT(DOW FROM t.ins_dt::date ) = 5 then '금' when EXTRACT(DOW FROM t.ins_dt::date ) = 6 then '토' end ) as week_type from ( select count(user_seq) as regit_cnt ,TO_CHAR(ins_dt, 'YYYYMMDD') as ins_dt ,count(case when os_type='AOS' then os_type end) as os_type_aos ,count(case when os_type='IOS' then os_type end) as os_type_ios from t_user_info where 1=1
and (date_trunc('week', now())+ '-1 days'::interval)::date < ins_dt and ins_dt < (date_trunc('week', now())+ '7 days'::interval)::date
group by TO_CHAR(ins_dt, 'YYYYMMDD') order by ins_dt asc ) t
