mapper.insert:
/* 상점 저장하기 insertStoreInfo */
	INSERT INTO t_store_info
	  (  
	      store_sql
	    , store_nm
	    , ctgry_nm
	    , road_addr
	    , road_zip
	    , company_no
	    , store_open_time
	    , store_end_time
	    , store_holiday_type
	    , max_cnt
	    , ins_seq
	    , ins_dt
	    , upt_seq
	    , upt_dt
	    , latitude
	    , longitude
	    , area_tot
	    , store_tel
	  )
	VALUES
	  (
	     (select max(store_seq) + 1  from t_store_info)
	    , #{storeNm,jdbcType=VARCHAR}
	    , #{ctgryNm,jdbcType=VARCHAR}
	    , #{roadAddr,jdbcType=VARCHAR}
	    , #{zip,jdbcType=VARCHAR}
	    , #{companyNo,jdbcType=VARCHAR}
	    , #{storeOpenTime,jdbcType=VARCHAR}
	    , #{storeEndTime,jdbcType=VARCHAR}
	    , #{storeHolidayType,jdbcType=VARCHAR}
	    , #{maxCnt,jdbcType=VARCHAR}
	    , 0 
	    , NOW()
	    , 0 
	    , NOW()
	    , #{latitude,jdbcType=VARCHAR}
	    , #{longitude,jdbcType=VARCHAR}
	    , #{areaTot,jdbcType=VARCHAR}
	    , #{storeTel,jdbcType=VARCHAR}
	  )

mapper.update:
  	/* TStoreInfo.updateRoadAddrByPrimaryKey   STORE_INFO 좌표 및 상세 주소 업데이트 */

	UPDATE 	t_store_info	
	SET		latitude = #{latitude,jdbcType=VARCHAR}
			,longitude = #{longitude,jdbcType=VARCHAR}
			,road_addr_detail1 = #{roadAddrDetail1,jdbcType=VARCHAR}
			,road_addr_detail2 = #{roadAddrDetail2,jdbcType=VARCHAR}
			,road_addr_detail3 = #{roadAddrDetail3,jdbcType=VARCHAR}
			,road_addr_detail4 = #{roadAddrDetail4,jdbcType=VARCHAR}
			,road_addr_detail5 = #{roadAddrDetail5,jdbcType=VARCHAR}
			,road_addr_detail6 = #{roadAddrDetail6,jdbcType=VARCHAR}
			,upt_dt = now()
			,vworld_response = #{vworldResponse,jdbcType=VARCHAR}
			,vworld_proc_dt = now()
	WHERE store_seq = #{storeSeq,jdbcType=BIGINT}

  	/* TStoreInfo.updateRoadAddrByPrimaryKey   STORE_INFO 좌표 및 상세 주소 업데이트 */

	UPDATE 	t_store_info	
	SET		vworld_response = #{vworldResponse,jdbcType=VARCHAR}
			,vworld_proc_dt = now()
	WHERE store_seq = #{storeSeq,jdbcType=BIGINT}

/* TStoreInfo.updateCafeNameUpdate STORE_INFO '카페' 상호명 기준 업데이트 */
